<?phprequire $_SERVER['DOCUMENT_ROOT'] . '/conns/guitar1.php';function deletePerson($email) {    global $db;    global $link;    try {        $sql = "DELETE FROM                           $db.people                                   WHERE peoEmail = :peoEmail";               $stmt = $link->prepare($sql);        $stmt->bindvalue(':peoEmail', $email);        $stmt->execute();        $deleteresult = $stmt->rowCount();               $stmt->closeCursor();         return $deleteresult;    } catch (PDOException $e) {        $deleteresult = 0;    }        if ($deleteresult >= 1) {        return 1;     } else {        return 0;    }}?>